import React, { useState } from "react";
import { Col, Row, Container, Card, Button, Alert } from "react-bootstrap";
import Search from "./components/Search";

export default function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [movies, setMovies] = useState(null);
  const [nominations, setNomination] = useState([]);
  const [title, setTitle] = useState(false);

  return (
    <Container style={{ marginTop: "80px" }}>
      <Row style={{ marginBottom: "15px" }}>
        <h1>The Shoppies</h1>
      </Row>
      <Row>
        <Col xs={12}>
          <Search
            searchTerm={searchTerm}
            setSearchTerm={setSearchTerm}
            setTitle={setTitle}
            setMovies={setMovies}
          />
        </Col>
      </Row>
      <Row style={{ marginTop: "20px" }}>
        {title && (
          <Col xs={6}>
            <Card style={{ padding: "20px" }}>
              <Card.Title>Movie Results for {title}</Card.Title>
              <ul>
                {movies &&
                  movies.map((result) => {
                    const movie = result.Title + ", " + result.Year + " ";
                    return (
                      <li key={result.imdbID} style={{ padding: "12px" }}>
                        {movie}
                        <Button
                          variant="outline-secondary"
                          size="sm"
                          onClick={() =>
                            setNomination((nominations) => [
                              ...nominations,
                              movie,
                            ])
                          }
                          disabled={
                            !nominations.includes(movie) &&
                            nominations.length < 5
                              ? false
                              : true
                          }
                        >
                          Nominate
                        </Button>
                      </li>
                    );
                  })}
              </ul>
            </Card>
          </Col>
        )}
        {title && (
          <Col xs={6}>
            <Card style={{ padding: "20px" }}>
              <Card.Title>Nominations</Card.Title>
              <ul>
                {nominations &&
                  nominations.map((nomination) => {
                    return (
                      <li key={nomination} style={{ padding: "12px" }}>
                        {nomination}{" "}
                        <Button
                          variant="outline-secondary"
                          size="sm"
                          onClick={() =>
                            setNomination(
                              nominations.filter(
                                (title) => title !== nomination
                              )
                            )
                          }
                        >
                          Remove
                        </Button>
                      </li>
                    );
                  })}
              </ul>
            </Card>
            {nominations.length >= 5 && (
              <Alert style={{ marginTop: "20px" }} variant="success">
                You've Completed Your 5 Nominations
              </Alert>
            )}
          </Col>
        )}
      </Row>
    </Container>
  );
}
